class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    unordered_map<long long int, long long int> andCount;
    long long int count = 0;
    for (int num : nums) {
        unordered_map<long long int, long long int> newAndCount;
        if (num == k) {
            ++count;
        }
        for (auto& [key, value] : andCount) {
            long long int newAnd = key & num;
            if (newAnd == k) {
                count += value;
            }
            newAndCount[newAnd] += value;
        }
        newAndCount[num]++;
        andCount = move(newAndCount);
    }
    return count;
    }
};
