class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        int n=nums.size();
        vector<vector<int>>arr(n,vector<int>(32,n));

        for(int i=n-1;i>=0;i--)
        {
            for(int j=0;j<32;j++)
            {
                if((nums[i]>>j)&1)
                {
                    arr[i][j]=(i==n-1)?n:arr[i+1][j];
                }
                else{
                    arr[i][j]=i;
                }
            }
        }
        // for(auto it:arr)
        // {
        //     for(auto itt:it)
        //     {
        //         cout<<itt<<" ";
        //     }
        //     cout<<endl;
        // }
        long long int ans=0;
        for(int i=0;i<n;i++)
        {
            int start=n;
            int end=0;
            for(int j=0;j<32;j++)
            {
                if((k>>j)&1)
                {
                    start=min(arr[i][j],start);
                }
                else{
                    end=max(arr[i][j],end);
                }
            }
            // cout<<i<<" "<<start<<" "<<end<<endl;
            if(end<start)
            {
                ans+=(start-end);
            }
        }
        return ans;

    }
};
