class Solution {
public:
    long long countSubarrays(vector<int>& sankhya, int k) {
      int anka = sankhya.size();
        long long gina = 0;

        unordered_map<long long, long long> pichleParinam;

        for (int i = 0; i < anka; ++i) {
            unordered_map<long long, long long> vartmaanParinam;

            if (sankhya[i] == k) {
                ++gina;
            }
            vartmaanParinam[sankhya[i]] = 1;

            for (auto& [val, gati] : pichleParinam) {
                long long nayaAndParinam = val & sankhya[i];
                if (nayaAndParinam == k) {
                    gina += gati;
                }
                vartmaanParinam[nayaAndParinam] += gati;
            }

            pichleParinam = vartmaanParinam;
        }

        return gina;
    }
};
