class Solution {
public:
     long long countSubarrays(vector<int>& a, int k) {
         int n=a.size();
         long long x=0;
         map<long long,long long>m;
         for(int i=0;i<n;i++){
             map<long long,long long>m1;
             if(a[i]==k){x++;}
             m1[a[i]]=1;
             for(auto it=m.begin();it!=m.end();it++){
                 long long s= (it->first)&a[i];
                 if(s==k){x+=it->second;}
                 m1[s]+=it->second;}
             m=m1;
         } 


         return x;

    }
};
