class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        map<long long, long long> prev;
        for(int i=0; i<nums.size(); i++){
            map<long long, long long> curr;
            if(nums[i]==k) ans++;
            curr[nums[i]]++;

            for(auto j:prev){
                long long res = j.first&nums[i];
                if(res==k) ans += j.second;
                curr[res] += j.second;
            }

            prev = curr;
        }
        return ans;
    }
};
