class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long ans = 0;
        List<int[]> list = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            int x = nums[i];
            List<int[]> nlist = new ArrayList<>();
            if (x >= k) {
                nlist.add(new int[]{x, 1});
            }
            for (int j = 0; j < list.size() && (x = list.get(j)[0] & x) >= k; j++) {
                if (nlist.size() > 0 && nlist.get(nlist.size() - 1)[0] == x) {
                    nlist.get(nlist.size() - 1)[1] += list.get(j)[1];
                } else {
                    nlist.add(new int[]{x, list.get(j)[1]});
                }
            }
            list = nlist;
            // for (int j = 0; j < list.size(); j++) {
            //     System.out.println(list.get(j)[0] + "*"+list.get(j)[1]);
            // }
            if (list.size() > 0 && list.get(list.size() - 1)[0] == k) {
                ans += list.get(list.size() - 1)[1];
            }
        }
        return ans;
    }
}
