use std::collections::HashMap;

impl Solution {
    pub fn count_subarrays(input: Vec<i32>, target: i32) -> i64 {
        let size = input.len();
        let mut total: i64 = 0;

        let mut p_m: HashMap<i64, i64> = HashMap::new();
        for &ele in input.iter() {
            let mut c_m: HashMap<i64, i64> = HashMap::new();

            if ele == target {
                total += 1;
            }

            *c_m.entry(ele as i64).or_insert(0) += 1;

            for (&key, &val) in p_m.iter() {
                let a_r = key & (ele as i64);
                if a_r == target as i64 {
                    total += val;
                }
                *c_m.entry(a_r).or_insert(0) += val;
            }

            p_m = c_m;
        }

        total
    }
}
