#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<ll,ll>dp;
        ll ans=0;
        for(int i=0;i<nums.size();i++){
            if(nums[i]==k){
                ans++;
            }

        }
        for(int i=0;i<nums.size();i++){
            map<ll,ll>next;
            next[nums[i]]=1;
            for(auto&it:dp){
                if((it.first&nums[i])==k){
                    ans+=it.second;
                }
                next[it.first&nums[i]]+=it.second;
            }
            dp=next;
        }
        return ans;

    }
};
