class Solution {
public:
long long countSubarrays(vector<int>& nums, int k) {
    unordered_map<int, long long>h;
    long long res=0;
    for(auto v:nums){
        unordered_map<int, long long>help;
        for(auto p:h){
            help[v&p.first]+=p.second;
        }
        help[v]++;
        res+=help[k];
        h=help;
    }
    return res;

}
};
