#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans = 0;
        map<ll,ll> mp,nmp;
        mp[nums[n-1]] = 1;
        if(k == nums[n-1])
            ++ans;
        for(int i=n-2; i>=0; --i)
        {
            nmp.clear();
            ++nmp[nums[i]];
            for(auto it : mp)
                nmp[(it.first&nums[i])] += it.second;
            ans += nmp[k];
            mp = nmp;
        }
        return ans;
    }
};
