#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
#define forn(i, x, n) for (int i = x; i < n; i++)
#define vi vector<int>
#define pb push_back
#define INF 1e9
#define fori1(n) for (ll i = 1; i <= n; i++)
#define fori(n) for (ll i = 0; i < n; i++)

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
 ll count = 0;

 unordered_map<ll, ll> prev;
for (int i = 0; i < n; ++i) {
unordered_map<ll, ll> curr; 

if (nums[i] == k) {
++count;
}
curr[nums[i]] = 1;

for (auto& [val, freq] : prev) {
ll neww = val & nums[i];
if (neww == k) {
count += freq;}

curr[neww] += freq;
}

prev = curr;} return count;
    }
};
