class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int len = array.size();
        long long total = 0;
        unordered_map<long long, long long> prevresult;
        for (int i = 0; i < len; ++i) {
            unordered_map<long long, long long> current; 
            if (array[i] == target) {
                ++total;
            }
            current[array[i]] = 1;
            for (auto& [value, freq] : prevresult) {
                long long newResult = value & array[i];
                if (newResult == target) {
                    total += freq;
                }
                current[newResult] += freq;
            }

            prevresult = current;
        }
        return total;
    }
};
