class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int n=nums.size();
       long long int c=0;
        unordered_map<long long int,long long int> m;
        for(int i=0;i<n;i++){
             unordered_map<long long int,long long int> m1;
            if(nums[i]==k)
                c++;
            m1[nums[i]]=1;
            for(auto it:m){
                long long int res=it.first&nums[i];
                if(res==k)
                    c+=it.second;
                if(m1.find(res)!=m1.end()){
                    m1[res]+=it.second;
                }
                else{
                    m1[res]=it.second;
                }
            }
            m=m1;
        }
        return c;
    }
};
