class Solution {
public:
    long long countSubarrays(std::vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<int, int> f;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> n;

            if (nums[i] == k) {
                ++count;
            }
            n[nums[i]]++;

            for (auto& it : f) {
                int x = it.first & nums[i];
                n[x] += it.second;
                if (x == k) {
                    count += it.second;
                }
            }

            f = move(n);
        }

        return count;
    }
};
