#define lln long long int

class Solution {
public:
    lln countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        lln c = 0;
        unordered_map<lln, lln> p,q;
        for (int i=0;i<n;i++) {
            q.clear();
            if(a[i]==k) {
                ++c;
            }
            q[a[i]] = 1;
            for (auto it : p) {
                lln nAnd = it.first&a[i];
                if (nAnd==k) {
                    c+=it.second;
                }
                q[nAnd]+=it.second;
            }
            p=q;
        }

        return c;
    }
};
