class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n=a.size();
        map<int,int>mp;
        long long ans=0;
        for(int i=0;i<n;i++){
            map<int,int>curr;
            if(a[i]==k){
                ans++;
            }
            curr[a[i]]=1;
            for(auto j:mp){
                long long currand=j.first&a[i];
                if(currand==k){
                    ans+=j.second;
                }
                curr[currand]+=j.second;
            }
            mp=curr;
        }
        return ans;
    }
};
