class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long, long> map1;
        for(int i=0;i<n;i++){
            unordered_map<long, long> map2;
            if(nums[i] == k)    ans++;

            map2[(long)nums[i]] = 1L;
            for(auto& entry : map1) {
                long key = entry.first;
                long value = entry.second;

                long res = key & nums[i];
                if(res == k) ans += value;
                map2[res] += map1[key];
            }
            map1 = move(map2);
        }
        return ans;
    }
};
