#include <ranges>
class Solution {
public:
  long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    vector<pair<int, int>> res;
    long long ans = 0;
    for (int i = 0; i < n; i++) {
      for (int j = 0; j < int(res.size()); j++) {
        res[j].first &= nums[i];
      }
      res.emplace_back(nums[i], i);
      res.resize(unique(res.begin(), res.end(), [&](auto&& x, auto&& y) {
        return x.first == y.first;
      }) - res.begin());
      int last = i + 1;
      for (auto [val, at] : res | views::reverse) {
        int len = last - at;
        if (val == k) {
          ans += len;
        }
        last = at;
      }
    }
    return ans;
  }
};
