class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        z = [[-1] * 32 for _ in range(n + 1)]
        for i in range(n):
            for j in range(32):
                z[i + 1][j] = z[i][j] if nums[i] >> j & 1 else i
        ans = 0
        for i in range(n):
            l, r = 0, i
            for j in range(32):
                if k >> j & 1:
                    l = max(l, z[i + 1][j] + 1)
                else:
                    r = min(r, z[i + 1][j])
            ans += max(0, r - l + 1)
        return ans
