class Solution {
public:
    long long countSubarrays(vector<int>& array, int k) {

        int size = array.size();
        long long total = 0;

        unordered_map<long long, long long> prev;

        for (int index = 0; index < size; ++index) {

            unordered_map<long long, long long> curr; 
            if (array[index] == k) {
                ++total;
            }
            curr[array[index]] = 1;

            for (auto& [value, frequency] : prev) {
                long long newAndResult = value & array[index];
                if (newAndResult == k) {
                    total += frequency;
                }
                curr[newAndResult] += frequency;
            }

            prev = curr;
        }
        return total;
    }

};
