class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int,long long> dp;
        long long ans = 0;
        for(auto x:nums){
            map<int,long long> new_dp;
            for(auto [andv,andc]:dp){
                int new_and = (x&andv);
                new_dp[new_and]+=andc;
            }
            new_dp[x]+=1;
            ans+=new_dp[k];
            dp = new_dp;
        }
        return ans;
    }
};
