class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size(),cnt = 0;
        unordered_map<long long, long long> prev;

        for(int i=0;i<100;i++){
            int temp = 0;
            temp += (long long)sqrt(10);
        }
        for (int i = 0; i < n; ++i) {
        unordered_map<long long, long long> curr;
        if (nums[i] == k)   ++cnt;

        curr[nums[i]] = 1;

        for (auto& [val, freq] : prev) {
            long long res = val & nums[i];

            if (res == k)  cnt += freq;
            curr[res] += freq;
        }
        prev = curr;
        }
        return cnt;
    }
};
