class Solution {
public:
   long long countSubarrays(vector<int>& arr, int target) {
        int size = arr.size();
        long long subarrayCount = 0;

        unordered_map<long long, long long> pr;

        int index = 0;
        while (index < size) {
            unordered_map<long long, long long> cr; 

            if (arr[index] == target) {
                ++subarrayCount;
            }
            cr[arr[index]] = 1;

            auto it = pr.begin();
            while (it != pr.end()) {
                long long andResult = it->first & arr[index];
                if (andResult == target) {
                    subarrayCount += it->second;
                }
                cr[andResult] += it->second;
                ++it;
            }
            pr = move(cr);
            ++index;
        }

        return subarrayCount;
    }
};
