var countSubarrays = function(nums, k) {
    let size = nums.length;
    let total = 0;
    let previousResults = new Map();
    for (let index = 0; index < size; ++index) {
        total += countSubarraysWithTarget(nums, index, k, previousResults);
        updatePreviousResults(previousResults, nums[index]);
    }
    return total;
};
function countSubarraysWithTarget(nums, index, k, previousResults) {
    let total = 0;
    let target = k;
    total += countExactMatches(nums[index], target);
    let currentResults = new Map();
    currentResults.set(nums[index], 1);
    for (let [value, frequency] of previousResults) {
        let newAndResult = value & nums[index];
        total += countMatches(newAndResult, target, frequency);
        if (currentResults.has(newAndResult)) {
            currentResults.set(newAndResult, currentResults.get(newAndResult) + frequency);
        } else {
            currentResults.set(newAndResult, frequency);
        }
    }
    previousResults.clear();
    for (let [key, value] of currentResults) {
        previousResults.set(key, value);
    }
    return total;
}
function updatePreviousResults(previousResults, num) {
    let newResults = new Map();
    for (let [value, frequency] of previousResults) {
        let newAndResult = value & num;
        if (newResults.has(newAndResult)) {
            newResults.set(newAndResult, newResults.get(newAndResult) + frequency);
        } else {
            newResults.set(newAndResult, frequency);
        }
    }
    previousResults.clear();
    for (let [key, value] of newResults) {
        previousResults.set(key, value);
    }
}
function countExactMatches(num, target) {
    return (num === target) ? 1 : 0;
}
function countMatches(newAndResult, target, frequency) {
    return (newAndResult === target) ? frequency : 0;
}
