class Solution(object):
    def countSubarrays(self, nums, k):
        s = len(nums)
        cnt = 0
        previous_res = {}
        for i in range(s):
            current_res = {}
            if nums[i] == k:
                cnt += 1
            current_res[nums[i]] = 1
            for value, frequency in previous_res.items():
                new_and_res = value & nums[i]
                if new_and_res == k:
                    cnt += frequency
                current_res[new_and_res] = current_res.get(new_and_res, 0) + frequency
            previous_res = current_res
        return cnt
