class Solution {
    public long countSubarrays(int[] nums, int k) {
        int[] c0 = new int[30];
        int i = 0;
        long ans = 0;
        for (int x : nums) {
            ++i;
            for (int b = 0; b < 30; ++b) {
                if ((k & 1 << b) != 0) {
                    if ((x & 1 << b) == 0) {
                        i = -1;
                        break;
                    }
                }
            }
            if (i < 0) {
                i = 0;
                Arrays.fill(c0, 0);
                continue;
            }
            int mi = i;
            for (int b = 0; b < 30; ++b) {
                if ((k & 1 << b) == 0) {
                    if ((x & 1 << b) == 0) {
                        c0[b] = i;
                    } else {
                        mi = Math.min(mi, c0[b]);
                    }
                }
            }
            ans += mi;
        }
        return ans;
    }
}
