#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll cnt=0;
        unordered_map<ll,ll>mp;
        for(int i=0;i<n;i++){
            unordered_map<ll,ll>mp1;
            int c=nums[i];
            // if(nums[i]<k)continue;
            if(nums[i]==k)cnt++;
            mp1[nums[i]]++;
            for(auto&x:mp){
                ll a=x.first&nums[i];
                if(a==k){
                    cnt+=x.second;
                }
                mp1[a]+=x.second;
            }
            mp=mp1;
        }

        return cnt;
    }
};
