class Solution {
    int check(vector<int>&v){
        int ans=0;
        for(int i=0;i<v.size();i++){
            if(v[i]==0){
                ans=ans|(1<<i);
            }
        }
        return ans;
    }
    void update(vector<int>&v,int no){
        for(int i=0;i<31;i++){
            int k=(no>>i)&1;
            if(k==0){
                v[i]++;
            }
        }
    }
    void update2(vector<int>&v,int no){
       for(int i=0;i<31;i++){
            int k=(no>>i)&1;
            if(k==0){
                v[i]--;
            }
        } 
    }
    int solve(vector<int>&nums,int k){
       long long ans=0;
        vector<int>v(31,0);
        int i=0;
        int j=0;
        while(j<nums.size()){
            update(v,nums[j]);
            while(i<j&&check(v)<k){
                update2(v,nums[i]);
                i++;
            }
            ans++;
            j++;
        }
        return ans; 
    }
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        map<long long, long long>mp1;
        for (int i = 0; i < n; ++i) {
            map<long long, long long>mp2; 
            if (nums[i] == k) {
                ans++;
            }
            mp2[nums[i]] = 1;
            for (auto it : mp1) {
                long long temp = it.first & nums[i];
                if (temp == k) {
                    ans +=it.second;
                }
                mp2[temp] += it.second;
            }

            mp1 = mp2;
        }
        return ans;
    }
};
