class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int sz = nums.size();
        long long total = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < sz; ++i) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                ++total;
            }

            curr[nums[i]] = 1;
            for (auto pr: prev) {
                int value = pr.first;
                int frequency = pr.second;
                long long newAndResult = value & nums[i];
                if (newAndResult == k) {
                    total += frequency;
                }
                curr[newAndResult] += frequency;
            }

            prev = curr;
        }
        return total;
    }
};
