public class Solution {
    public long CountSubarrays(int[] nums, int k) {
        int n = nums.Length;
        long c = 0;
        Dictionary<long, long> p = new Dictionary<long, long>();
        for (int i = 0; i < n; ++i) {
            Dictionary<long, long> q = new Dictionary<long, long>(); 
            if (nums[i] == k) {
                ++c;
            }
            q[nums[i]] = 1;
            foreach (var entry in p) {
                long x = entry.Key;
                long y = entry.Value;
                long z = x & nums[i];
                if (z == k) {
                    c += y;
                }
                if (!q.ContainsKey(z)) {
                    q[z] = 0;
                }
                q[z] += y;
            }
            p = q;
        }

        return c;
    }
}
