class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n=arr.size();
        V<V<int>>dp(n+1,V<int>(32,0));
        for(int i=n-1;i>=0;i--){
            V<int>temp(32);
            int x=arr[i];
            int j=0;
            while(x){
                int r=x%2;
                temp[j]=r;
                j++;
                x=x/2;
            }
            for(j=0;j<32;j++){
                if(temp[j]==0)
                dp[i][j]=0;
                else
                dp[i][j]=dp[i+1][j]+1;
            }
        }
        ll res=0;
        V<int>bts(32);
        int j=0;
        while(k>0){
            int r=k%2;
            bts[j]=r;
            j++;
            k=k/2;
        }
        for(int i=0;i<n;i++){
            int mx_z=0;
            int min_o=n-i;
            for(j=0;j<32;j++){
                if(bts[j]==0){
                    mx_z=max(mx_z,dp[i][j]);
                }
                else{
                    min_o=min(min_o,dp[i][j]);
                }
            }
            ll temp=min_o-mx_z;
            temp=max(0LL,temp);
            res+=temp;
        }
        return res;



    }
};
