from collections import defaultdict

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:

        n = len(nums)
        count = 0
        freq = defaultdict(int)

        for num in nums:
            new_freq = defaultdict(int)

            if num == k:
                count += 1

            for and_val in freq:
                new_and_val = and_val & num
                new_freq[new_and_val] += freq[and_val]
                if new_and_val == k:
                    count += freq[and_val]

            new_freq[num] += 1  
            freq = new_freq  

        return count
