class Solution {
public:
    long long countSubarrays(vector<int>& v, int target) {
        int size = v.size();
        unordered_map<long long, long long> previousResults;
        long long total = 0;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currentResults; 
            if (v[index] == target) {
                ++total;
            }
            currentResults[v[index]] = 1;
            for (auto& [value, frequency] : previousResults) {
                long long newAndResult = value & v[index];
                if (newAndResult == target) {
                    total += frequency;
                }
                currentResults[newAndResult] = currentResults[newAndResult] + frequency;
            }

            previousResults = currentResults;
        }
        return total;
    }
};
