class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        set<int> s;
        int n = v.size();
        long long ans = 0;

        map<int, int> mp;

        for (int i = 0; i < n; ++i) {
            set<int> q;

            q.insert(v[i]);
            if (v[i] == k) {
                ans++;
            }
            map<int, int> new_mp;
            new_mp[v[i]]++;

            for (auto z : s) {
                int new_and = z & v[i];
                q.insert(new_and);
                new_mp[new_and] += mp[z];
                if (new_and == k) {
                    ans += mp[z];
                }
            }

            mp = new_mp;
            s = q;
        }

        return ans;
    }
};
