import java.util.HashMap;
import java.util.Map;

public class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count = 0;

        Map<Long, Long> prevResults = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            Map<Long, Long> currResults = new HashMap<>();

            if (nums[i] == k) {
                ++count;
            }
            currResults.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : prevResults.entrySet()) {
                long newAndResult = entry.getKey() & nums[i];
                if (newAndResult == k) {
                    count += entry.getValue();
                }
                currResults.put(newAndResult, currResults.getOrDefault(newAndResult, 0L) + entry.getValue());
            }

            prevResults = currResults;
        }

        return count;
    }
}
