#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll cnt = 0;
        unordered_map<ll, ll> mp;
        for (int i = 0; i < n; i++) {
            unordered_map<ll, ll> curr;
            if (nums[i] == k) cnt++;
            curr[nums[i]] = 1;

            for (auto &[x, y] : mp) {
                ll res = x & nums[i];
                if (res == k)
                    cnt += y;
                curr[res] += y;
            }
            mp = curr;
        }
        return cnt;
    }
};
