class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = 0
        a = []
        for i,num in enumerate(nums):
            b = []
            idx = -1
            for val,pos in a:
                new_val = num & val
                if not b or b[-1][0] != new_val:
                    if new_val == k:
                        idx = len(b)
                    b.append([new_val, pos])
            if not b or b[-1][0] != num:
                if num == k:
                    idx = len(b)
                b.append([num, i])                
            if idx >= 0:
                start = b[idx][1]
                end = b[idx+1][1] if idx + 1 < len(b) else i + 1
                res += end - start
            a = b
        return res
