class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res = 0;
        unordered_map<int, int> count;
        for(int n: nums) {
            unordered_map<int, int> tmp;
            for(auto [x, c]: count) {
                tmp[x & n] += c;
            }
            tmp[n] += 1;
            count = tmp;
            res += count[k];
        }
        return res;
    }
};
