#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int t) {
        int n = nums.size();
        ll sum = 0;
        unordered_map<ll, ll> mp;

        for (int i = 0; i < n; i++) {
            unordered_map<ll, ll> temp;

            if (nums[i] == t) {
                ++sum;
            }

            temp[nums[i]] += 1;

            for (auto& it: mp) {
                ll val = it.first;
                ll count = it.second;
                ll curr = val & nums[i];
                if (curr == t) {
                    sum += count;
                }
                temp[curr] += count;
            }

            mp = temp;
        }

        return sum;
    }
};
