class Solution {
    public long countSubarrays(int[] arr, int k) {
        int n = arr.length;
        int[][] dp = new int[n][32];
        int[] c = new int[n];
        for (int i = n-1; i >= 0; i--) {
            boolean f = true;
            int x = arr[i];
            int y = k;
            while(y>0) {
                if(y%2==1 && x%2!=1) {
                    f=false; break;
                }
                x = x>>1;
                y = y>>1;
            }
            if(!f) {
                arr[i] = -1;
                c[i] = -1;
            }
            else {
                if(i==n-1 || c[i+1]==-1) c[i]=i;
                else c[i]=c[i+1];
            }
            if(f) {
                x = arr[i];
                if(i==n-1 || arr[i+1]==-1) {
                    for (int j = 0; j < 32; j++) {
                        if(x%2==0) dp[i][j]=i;
                        else dp[i][j]=-1;
                        x = x>>1;
                    }
                }
                else {
                    for (int j = 0; j < 32; j++) {
                        if((x>>j)%2==0) dp[i][j]=i;
                        else if(dp[i+1][j]>-1) dp[i][j]=dp[i+1][j];
                        else dp[i][j]=-1;
                    }
                }
            }
        }
        long sum = 0;
        for (int i = 0; i < n; i++) {
            if(arr[i]==-1) continue;
            int m = i, x=arr[i], y=k;
            for (int j = 0; j < 32; j++) {
                if((x>>j)%2==1 && (y>>j)%2==0) {
                    if(dp[i][j]==-1) {
                        m=-1; break;
                    }
                    else m = Math.max(dp[i][j],m);
                }
            }
            if(m>-1) {
                sum += c[i]-m+1;
            }
        }
        return sum;
    }
}
