class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;

        map<int, int> mp;

        for(int i : nums) {
            map<int, int> nmp;

            for(auto [x, y]: mp) {
                int nx = x & i;
                nmp[nx] += y;
            }
            nmp[i]++;
            // mp.clear();
            mp = nmp;
            ans += mp[k];
        }

        return ans;
    }
};
