class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long ans=0;
        unordered_map<int,int>m;
        for(int i=0;i<a.size();i++)
        {
              unordered_map<int,int>m1;
            for(auto i1:m)
            {
                m1[i1.first&a[i]]+=i1.second;

            }
            m1[a[i]]++;
            m=m1;
            for(auto i:m)
            {
                if(i.first==k)
                    ans+=(i.second);
            }

        }
        return ans;

    }
};
