class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>arr(32, -1);
        long long ans=0;
        for(int i=0;i<n;i++){
            int index=0;
            for(int j=0;j<32;j++){
                if(((nums[i]>>j)&1)==0) arr[j]=i;
            }
            // for(int j=2;j>=0;j--){
            //     cout<<arr[j]<<" ";
            // }
            // cout<<endl;
            int firstoneindex=0, firstzeroindex=i;
            for(int j=0;j<32;j++){
                if(((k>>j)&1)==1){
                    if(arr[j]!=-1){
                        firstoneindex=max(firstoneindex, arr[j]+1);
                    }
                }else{
                    if(arr[j]!=-1){
                        firstzeroindex=min(firstzeroindex, arr[j]);
                    }else{
                        firstzeroindex=-1;
                    }
                }
            }
            // cout<<firstoneindex<<" "<<firstzeroindex<<endl;
            ans+=max(0,firstzeroindex-firstoneindex+1);
        }
        return ans;
    }
};
