class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        c=0
        prev={}
        for i in range(len(nums)):
            curr = {}
            if nums[i] == k:
                c+=1
            curr[nums[i]] = 1
            for val, num in prev.items():
                temp = val&nums[i]
                if temp in curr:
                    curr[temp] += num
                else:
                    curr[temp] = num
                if temp == k:
                    c += num
            prev = curr
        return c
