class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        map<long long,long long>mp;
        int n=arr.size();
        long long ans=0;
        for(int i=0;i<n;i++){
            for(long long j=0;j<32;j++){
                int p=(arr[i]>>j)&1;
                if(p==1)mp[j]++;
                else mp[j]=0;
                // if(p==0)mp[0]++;
                // else mp[0]=0;
            }
            // cout<<mp[0]<<" ";
            long long mi=i+1,mx=0;
            for(long long j=0;j<32;j++){
                int p=(k>>j)&1;
                if(p==1)mi=min(mi,mp[j]);
                if(p==0)mx=max(mx,mp[j]);
                // cout<<mi<<" "<<
            }
            cout<<mi<<endl;
            ans+=max((long long)0,mi-mx);
        }
        // cout<<endl;
        return ans;
    }
};
