class Solution {
public:
	#define lli long long int
    lli countSubarrays(vector<int>& ip, int tar) {
        int size = ip.size();lli ans = 0;

        unordered_map<lli, lli> prev;

        for (int index = 0; index < size; ++index) {
            unordered_map<lli, lli> curr; 

            if (ip[index] == tar)++ans;
            curr[ip[index]] = 1;

            for (auto& [value, freq] : prev) {
                lli temp = value & ip[index];

                if (temp == tar) ans += freq;

                curr[temp] += freq;
            }

            prev = curr;
        }

        return ans;
    }
};
