class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;

        map<long long, long long> arr;
        long long newAndResult;
        int i = 0;
        while (i < n) {
            map<long long, long long> brr; 

            if (nums[i] == k) {
                ++ans;
            }
            brr[nums[i]] = 1;

            for (auto [val, f] : arr) {
                newAndResult = val & nums[i];
                if (newAndResult == k) {
                    ans += f;
                }
                brr[newAndResult] += f;
            }

            arr = brr;
            ++i;
        }

        return ans;
    }
};
