class Solution {
public:
    long long countSubarrays(vector<int>& anuja, int anuj_b) {
        int size = anuja.size();
        long long total = 0;
        unordered_map<long long, long long> previousResults;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currentResults; 
            if (anuja[index] == anuj_b) {
                ++total;
            }
            currentResults[anuja[index]] = 1;
            for (auto& [value, frequency] : previousResults) {
                long long newAndResult = value & anuja[index];
                if (newAndResult == anuj_b) {
                    total += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }
        return total;
    }
};
