class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n=nums.length;
        int[] last=new int[32];int st=0;
        int mt=2147483647;
        long ret=0L;
        for (int i=0;i<=31;i++){
            last[i]=-9999;
        }
        for (int i=0;i<nums.length;i++){
            if ((nums[i]&k)!=k){
                st=i+1;
            } else {

                int x=nums[i];
                for (int j=0;j<31;j++){
                    if (x%2==0){
                        last[j]=i;
                    }
                    x/=2;
                }
                int mi=9999999;
                int tx=k;
                for (int j=0;j<31;j++){
                    if (tx%2==0 && last[j]<mi){
                        mi=last[j];
                    }
                    tx/=2;
                }
               // System.out.println(mi+" "+st+" "+i);
                if (mi>=st){
                    ret=ret+mi-st+1;
                }
            }
        }
        return ret;
    }

}
