class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        unordered_map<int , long long> prev_mp , mp ;
        long long count {} ;
        int n{(int)nums.size()} ;

        for(int i {} ; i < n ;i++){
            if(nums[i] == k){
                // cout << i << endl ;
                count++ ;
            }
            mp[nums[i]]++ ;


            for(auto pr : prev_mp){
                if((pr.first&nums[i]) == k) {
                    // cout << pr.first << " " << pr.first<< endl ;
                    count += pr.second ;
                }
                mp[(pr.first&nums[i])] += pr.second ;
            }


            prev_mp = mp;
            mp.clear() ;
        }


        return count ;
    }
};
